jQuery(document).ready(function (jQuery) {


    // onload
    if (jQuery("#select_sku_format_maxminsku").attr("checked")) {
            jQuery('.form-table tr').eq(1).find('td').removeClass().addClass('tr-color-white-bg');
            jQuery('.form-table tr').eq(2).find('td').removeClass().addClass('tr-color-white-bg');
            jQuery('.form-table tr').eq(3).find('td').removeClass().addClass('tr-color-transparent-bg');
            jQuery('.form-table tr').eq(4).find('td').removeClass().addClass('tr-color-transparent-bg');         
    } else if (jQuery("#select_sku_format_charactersforsku").attr("checked")) {
            jQuery('.form-table tr').eq(3).find('td').removeClass().addClass('tr-color-white-bg');
            jQuery('.form-table tr').eq(4).find('td').removeClass().addClass('tr-color-white-bg');
            jQuery('.form-table tr').eq(1).find('td').removeClass().addClass('tr-color-transparent-bg');
            jQuery('.form-table tr').eq(2).find('td').removeClass().addClass('tr-color-transparent-bg');         
    }

    // On radio change
    jQuery('input[type=radio][name=atr_select_sku_format]').change(function() {
        if (jQuery("#select_sku_format_maxminsku").attr("checked")) {
            jQuery('.form-table tr').eq(1).find('td').removeClass().addClass('tr-color-white-bg');
            jQuery('.form-table tr').eq(2).find('td').removeClass().addClass('tr-color-white-bg');
            jQuery('.form-table tr').eq(3).find('td').removeClass().addClass('tr-color-transparent-bg');
            jQuery('.form-table tr').eq(4).find('td').removeClass().addClass('tr-color-transparent-bg');         
        } else if (jQuery("#select_sku_format_charactersforsku").attr("checked")) {
            jQuery('.form-table tr').eq(3).find('td').removeClass().addClass('tr-color-white-bg');
            jQuery('.form-table tr').eq(4).find('td').removeClass().addClass('tr-color-white-bg');
            jQuery('.form-table tr').eq(1).find('td').removeClass().addClass('tr-color-transparent-bg');
            jQuery('.form-table tr').eq(2).find('td').removeClass().addClass('tr-color-transparent-bg');             
        }
    })

});


